mapper.select:
-- 检查 10 分钟内用户是否提交过停车位状态信息 --
SELECT EXISTS(
               SELECT 1
               FROM temp
               WHERE temp_uid = #{uid}
                 AND temp_pid = #{pid}
                 AND temp_update_date > date_sub(now(), INTERVAL '0 00:10:00' DAY_SECOND)
           ) AS result;

-- 获取状态信息 --
SELECT *
FROM temp
WHERE temp_id = #{id}
  AND temp_status = #{status}

-- 检查状态信息 --
SELECT EXISTS(
               SELECT 1
               FROM temp
               WHERE temp_id = #{id}
                 AND temp_status = #{status}
           ) AS result;
