mapper.select:
SELECT EXISTS(
               SELECT 1
               FROM temp
               WHERE tmp_uid = #{uid}
                 AND tmp_pid = #{pid}
                 AND tmp_update_date > date_sub(now(), INTERVAL '0 00:10:00' DAY_SECOND)
           ) AS result;

SELECT EXISTS(
               SELECT 1
               FROM temp
               WHERE tmp_id = #{id}
           ) AS result;

mapper.update:
UPDATE temp
SET tmp_useful = tmp_useful + 1
WHERE tmp_id = #{tid};

UPDATE temp
SET tmp_unuseful = tmp_unuseful + 1
WHERE tmp_id = #{tid};
