mapper.select:
-- 检查用户是否存在 --
SELECT EXISTS(
               SELECT 1
               FROM user
               WHERE user_area_code = #{areaCode}
                 AND user_phone = #{phone}
           ) AS result;

-- 获取用户信息 --
SELECT *
FROM user
WHERE user_area_code = #{areaCode}
  AND user_phone = #{phone};
